// Bulma Carousel JavaScript - Placeholder
// Download from: https://github.com/VizuaaLOG/bulma-carousel

(function() {
  'use strict';
  
  // Simple carousel functionality
  function initCarousel() {
    const carousels = document.querySelectorAll('.carousel');
    
    carousels.forEach(carousel => {
      const items = carousel.querySelectorAll('.carousel-item');
      const prevBtn = carousel.querySelector('.carousel-navigation.is-prev');
      const nextBtn = carousel.querySelector('.carousel-navigation.is-next');
      
      let currentIndex = 0;
      
      function showItem(index) {
        items.forEach((item, i) => {
          item.classList.toggle('is-active', i === index);
        });
      }
      
      if (prevBtn) {
        prevBtn.addEventListener('click', () => {
          currentIndex = (currentIndex - 1 + items.length) % items.length;
          showItem(currentIndex);
        });
      }
      
      if (nextBtn) {
        nextBtn.addEventListener('click', () => {
          currentIndex = (currentIndex + 1) % items.length;
          showItem(currentIndex);
        });
      }
      
      // Show first item
      if (items.length > 0) {
        showItem(0);
      }
    });
  }
  
  // Initialize on DOM ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initCarousel);
  } else {
    initCarousel();
  }
})(); 